                                        <input
                                            type='text'
                                            className='form-control'
                                            name="filterName"
                                            value={(this.props.isShowList) ? "" : this.state.filterName}
                                            onChange={this.onChange}
                                        />
                                    </td>
                                    <td>
                                        <select
                                            className="form-control"
                                            name="filterStatus"
