import * as types from '../constants/ActionTypes';

var inintialState = null;

var itemEditting = (state = inintialState, action) => {
      switch (action.type) {
            case types.EDIT_TASK:
                  console.log(action.task)                
                  return action.task;            
            default:
                  return state;
      }

}

export default itemEditting;
